{
  "policy": {
    "name": "Expiration",
    "description": "Assigned to Group",
    "policyType": {
      "code": "lifecycle"
    },
    "config": {
        "lifecycleType": "fixed",
        "lifecycleAge": "2",
        "lifecycleRenewal": "7",
